<?php	include_once('../database.php');		$user 	= mysql_real_escape_string($_GET['user']);		$action 	= $_GET['action'];				if($action == 'getaccount'){		$sql = "	SELECT 			User.id as userid,									User.firstName AS firstname, 									User.lastName AS lastname, 									User.title, 									User.apiKey AS tokenid, 																		User.email, 									User.phone, 									DATE_FORMAT(User.deletedAt, '%m/%d/%Y') as userdeletedat, 									DATE_FORMAT(User.createdAt, '%m/%d/%Y') as usercreatedat,									Office.name as officename,									Office.id as officeid,																	Market.name AS marketname, 									Market.id AS marketid, 																	Corporation.name as corporationname,									Corporation.id as corporationid													FROM 			User					/*INNER JOIN officedefaults ON User.id = officedefaults.userid*/					INNER JOIN  	UserOffice ON UserOffice.userId = User.id					INNER JOIN  	Office ON UserOffice.officeId = Office.id					INNER JOIN 		Market ON Office.regionId = Market.id					INNER JOIN 		Corporation ON Market.corporationId = Corporation.id					WHERE 			Concat(User.email) = '{$user}'					AND 				Corporation.ID = 46					AND	 			User.override IS NULL					ORDER BY 		User.deletedAt					LIMIT 1";				mysql_query($sql);		$result 	= mysql_query($sql);		print mysql_error();		$cnt 		= mysql_num_rows($result);				if($cnt == 0){				$re[] = 0;			print json_encode($re);			return;		}				while($row = mysql_fetch_assoc($result)) {			$rows[] = $row;		}				print json_encode($rows);	}		///////////////////////////////////////////////////////////////////////			if($action == 'reactivate'){		$sql = "UPDATE User SET deletedAt = NULL where id  =". $user;				mysql_query($sql);				print '{"result":'.mysql_affected_rows().'}';		}?>