<?phpini_set('max_execution_time','0');ini_set("display_errors",1);//phpEXCEL Classesrequire_once 'Classes/PHPExcel.php';//GETTING PROPOSAL DATArequire_once 'service_data.php';$styleHead1 = array(    'font'  => array(        'bold'  => true,        'color' => array('rgb' => '000000'),        'size'  => 10    ));$styleHead2 = array(        'font'  => array(                'bold'  => true,                'color' => array('rgb' => 'FFFFFF'),                'size'  => 11));$styleBorderLines = array(           	'style' => PHPExcel_Style_Border::BORDER_THIN,           	'color' => array('rgb' => 'CCCCCCCC'));// json decode proposal data$proposalLines = json_decode($json_data,true);// passed proposal data to array variable$arrProposal = $proposalLines['proposal'];// create PHPExcel object$objExcel = new PHPExcel();// Set document properities	$objExcel->getProperties()->setCreator("Excel Export")	         ->setLastModifiedBy("ShowSeeker")	         ->setTitle("ShowSeeker")	         ->setSubject("Report")	         ->setDescription("Testing")	         ->setKeywords("Office 5 Excel")	         ->setCategory("Test Category");// page settings	$sheet = $objExcel->getActiveSheet();		$sheet->getPageSetup()->setOrientation(PHPExcel_Worksheet_PageSetup::ORIENTATION_PORTRAIT);	$sheet->getPageSetup()->setPaperSize(PHPExcel_Worksheet_PageSetup::PAPERSIZE_LETTER);	$sheet->getPageSetup()->setRowsToRepeatAtTopByStartAndEnd(1, 3);		$pageMargins = $sheet->getPageMargins();	$margin = 0.9 / 2.54;		$pageMargins->setTop($margin);	$pageMargins->setBottom($margin);	$pageMargins->setLeft($margin);	$pageMargins->setRight($margin);// Set active sheet index to the first sheet	$objExcel->setActiveSheetIndex(0);        // Assing style to header text	 $objRichText = new PHPExcel_RichText();    //CORPORATTION NAME    $corporation = trim($proposalLines['corporation'][0]['name']);    //CORPORATTION LOGO    $logo = trim($proposalLines['corporation'][0]['logo']);        $imgPath = str_replace('/services/downloads','',getcwd());    $logo = str_replace('http://ww2.showseeker.com',$imgPath.'/showseeker', $logo);	//COLLECTING USER INFO		 $objBold = $objRichText->createTextRun(trim($corporation));	 $objBold->getFont()->setSize(14)->setName('Verdana');    $user_name = $proposalLines['user'][0]['firstname'].' '.$proposalLines['user'][0]['lastname'];       $user_off_address 	= isset($proposalLines['user'][0]['officeaddress']) ? $proposalLines['user'][0]['officeaddress'] : '';       $user_state 			= isset($proposalLines['user'][0]['officestate']) ? $proposalLines['user'][0]['officestate'] : '';       $user_city	 			= isset($proposalLines['user'][0]['officecity']) ? $proposalLines['user'][0]['officecity'] : '';       $user_zip 				= isset($proposalLines['user'][0]['officezipcode']) ? $proposalLines['user'][0]['officezipcode'] : '';       $user_phone 			= isset($proposalLines['user'][0]['phone']) ? $proposalLines['user'][0]['phone'] : '';    $objBold = $objRichText->createTextRun(" \n".$user_name."");        $objBold->getFont()->setSize(8)->setName('Verdana');        $objNormal = $objRichText->createTextRun("\n".$user_off_address." \n".$user_city.", ".$user_state." ".$user_zip." \n".$user_phone."");        $objNormal->getFont()->setSize(8)->setName('Verdana');//	NUMBER OF PAGES		$objExcel->getActiveSheet()->getHeaderFooter()->setOddFooter('Page &P / &N');	$objExcel->getActiveSheet()->getHeaderFooter()->setEvenFooter('Page &P / &N');//// DISPLAYING CORPORATION LOGO	$objExcel->getActiveSheet()->mergeCells('A1:B1');	$objDrawing = new PHPExcel_Worksheet_Drawing();	$objDrawing->setPath($logo);	$objDrawing->setHeight(70);	$objDrawing->setWidth(180);	$objDrawing->setCoordinates('A1');	$objDrawing->setOffsetX(1);	$objDrawing->setWorksheet($objExcel->getActiveSheet());// Display user name and address in excel report	$objExcel->getActiveSheet()->mergeCells('C1:E1');	$objExcel->getActiveSheet()->getStyle('C1:E1')->getAlignment()->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_CENTER);	$objExcel->getActiveSheet()->setCellValue('C1',$objRichText);	$objExcel->getActiveSheet()->getRowDimension(1)->setRowHeight(60);	$objExcel->getActiveSheet()->getStyle('C1')->getAlignment()->setWrapText(true);	$objExcel->getActiveSheet()->setCellValue('C1',$objRichText);// Set bottom border to header row	$objExcel->getActiveSheet()->getStyle('A1:E1')->getBorders()->getBottom()->setBorderStyle(PHPExcel_Style_Border::BORDER_THIN);//Display Header line (proposal info) of excel report	$objExcel->getActiveSheet()->mergeCells('A2:E2');	$objExcel->getActiveSheet()->getStyle('A2:E2')->getAlignment()->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_CENTER);	$objExcel->getActiveSheet()->getStyle('A2:E2')->getAlignment()->setVertical(PHPExcel_Style_Alignment::VERTICAL_CENTER);			$proposalinfo = trim($proposalLines['proposalinfo']['name']);	$objExcel->getActiveSheet()->getRowDimension('2')->setRowHeight(20);	$objExcel->getActiveSheet()->setCellValue('A2',$proposalinfo);	$objExcel->getActiveSheet()->getStyle('A2')->applyFromArray($styleHead1);		// Set columns heading with grey background	$objExcel->getActiveSheet()->getRowDimension(3)->setRowHeight(25);	$objExcel->getActiveSheet()->getStyle('A3:E3')->applyFromArray($styleHead2);	$objExcel->getActiveSheet()->getStyle('A3:E3')->getFill()->applyFromArray(array('type' => PHPExcel_Style_Fill::FILL_SOLID,'startcolor' => array('rgb' => '333333')));	$objExcel->getActiveSheet()->getStyle('A3:E3')->getAlignment()->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_CENTER);	$objExcel->getActiveSheet()->getStyle('A3:E3')->getAlignment()->setVertical(PHPExcel_Style_Alignment::VERTICAL_CENTER);	$objExcel->getActiveSheet()->getColumnDimension('A')->setWidth(10);	$objExcel->getActiveSheet()->getColumnDimension('B')->setWidth(45);	$objExcel->getActiveSheet()->getColumnDimension('C')->setWidth(17);	$objExcel->getActiveSheet()->getColumnDimension('D')->setWidth(17);	$objExcel->getActiveSheet()->getColumnDimension('E')->setWidth(10);	$objExcel->getActiveSheet()->setCellValue('A3','Network');	$objExcel->getActiveSheet()->setCellValue('B3','Program');	$objExcel->getActiveSheet()->setCellValue('C3','DayPart');	$objExcel->getActiveSheet()->setCellValue('D3','Dates');	$objExcel->getActiveSheet()->setCellValue('E3','Rate');// FreezePane rows (Assign vertical scrolling from this column and row)$objExcel->getActiveSheet()->freezePane('A4');//Display first zone details$row = 4;// Printing actual proposal data (Zone wise)foreach($arrProposal as $proposalKey => $proposalValue) {                    $objExcel->getActiveSheet()->getStyle('A'.$row.':E'.$row.'')->getFont()->setBold(true);		  $objExcel->getActiveSheet()->mergeCells('A'.$row.':E'.$row.'');                        $zoneName = isset($proposalValue['zone']['zonename']) ? $proposalValue['zone']['zonename'] : '';        $objExcel->getActiveSheet()->setCellValue('A'.$row,'Zone : '.$zoneName);        $objExcel->getActiveSheet()->getStyle('A'.$row)->getAlignment()->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_RIGHT);        		  $objExcel->getActiveSheet()->getRowDimension($row)->setRowHeight(25);  		  $objExcel->getActiveSheet()->getStyle('A'.$row.':'.'E'.$row)->getAlignment()->setVertical(PHPExcel_Style_Alignment::VERTICAL_CENTER);    		  $objExcel->getActiveSheet()->getStyle('A'.$row.':'.'E'.$row)->getBorders()->getBottom()->applyFromArray($styleBorderLines);                $row++;        foreach($proposalValue['lines'] as $zoneKey => $zoneValue){				$objExcel->getActiveSheet()->getRowDimension($row)->setRowHeight(50);  								$objExcel->getActiveSheet()->getStyle('A'.$row.':'.'E'.$row)->applyFromArray(array('font'  => array('color' => array('rgb' => '000000'),'size'  => 8, 'name'  => 'Verdana')));				$objExcel->getActiveSheet()->getStyle('A'.$row.':'.'E'.$row)->getBorders()->getBottom()->applyFromArray($styleBorderLines);                				$objExcel->getActiveSheet()->getStyle('A'.$row.':'.'E'.$row)->getAlignment()->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_CENTER);				$objExcel->getActiveSheet()->getStyle('A'.$row.':'.'E'.$row)->getAlignment()->setVertical(PHPExcel_Style_Alignment::VERTICAL_CENTER);                            //Network            $objExcel->getActiveSheet()->setCellValue('A'.$row,$zoneValue['callsign']);                        //Program	         $title = $zoneValue['title'];                        $objExcel->getActiveSheet()->getStyle('B'.$row)->getAlignment()->setWrapText(true);            $objExcel->getActiveSheet()->getStyle('B'.$row)->getAlignment()->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_LEFT);            $objExcel->getActiveSheet()->setCellValue('B'.$row,rtrim($title,', '));                        //DayPart            $days = isset($zoneValue['dayFormat']) ? $zoneValue['dayFormat'] : '';            $dayPart = trim($days).' '.str_replace('M','',str_replace(':00','',date ('g:iA',strtotime($zoneValue['starttime'])))).'-'.str_replace('M','',str_replace(':00','',date ('g:iA',strtotime($zoneValue['endtime']))));            $objExcel->getActiveSheet()->setCellValue('C'.$row,$dayPart);                        //Dates		      $dates = date('n/j/y',strtotime($zoneValue['startdate'])).' - '.date('n/j/y',strtotime($zoneValue['enddate']));            $objExcel->getActiveSheet()->setCellValue('D'.$row,$dates);                        //Rate            $objExcel->getActiveSheet()->setCellValue('E'.$row,'=DOLLAR('.$zoneValue['rate'].')');            $row++;        }        $row++;}// Create and Save file$objWriter = PHPExcel_IOFactory::createWriter($objExcel, 'Excel5');$pslFileName = cleanStr($proposalinfo);$objWriter->save('/var/www/html/showseeker/files/tmp/'.$pslFileName.'.xls');echo '{"filename":"'.$pslFileName.'.xls"}';exit;function cleanStr($string) {   $string = str_replace(' ', '_', $string); // Replaces all spaces with hyphens.   return preg_replace('/[^A-Za-z0-9\-]/', '', $string); // Removes special chars.}?>