<?phpclass MYPDF extends TCPDF {		//Page header		public function Header() {					$this->Image($this->header_logo,'','',0,15,'','','',false,300,'right',false, false, 0);			$this->SetFont('helvetica', '', 8);			$this->writeHTMLCell(0, 0, '', '', $this->header_string , 0, 0, 0, true, 'R', false);			$this->SetLineStyle(array('width' => 0.5, 'cap' => 'butt', 'join' => 'miter', 'dash' => 0, 'color' => '#000000'));			$this->SetY(23);			$this->Cell(0, 0, '', 'T', 0, 'C');			$this->SetFont('helvetica', 'BI', 10);			$this->writeHTMLCell(0, 0, '', 25, $this->header_title , 0, 0, 0, true, 'C', false);			$this->SetFont('helvetica', 'B', 8);			$this->SetFillColor(0, 0, 0);			$this->SetTextColor(255, 255, 255);			$headerFlags = $this->header_logo_width;			$rw 	= ($headerFlags['hiderates']=='true')?1:0;			$rcw 	= ($headerFlags['showratecard']=='true')?0:1;			$x 	= $this->original_lMargin;  						$y 	= 32; 			$w 	= 15;				$h		= 10;						$this->MultiCell($w, $h, 'Network', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');			$x += $w; 			$w	=	(3*$rw)+$rcw+31;						$this->MultiCell($w, $h, 'DayPart', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');			$x += $w; 			$w	=	(4*$rw)+(2*$rcw)+50;						$this->MultiCell($w, $h, 'Show - Description', 1, 'L',1,0,$x,$y,true,0,false,true,$h,'M');						$x += $w; 						$w	=	(4*$rw)+$rcw+13;						$this->MultiCell($w, $h, 'Start', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');						$x += $w; 			$w	=	(4*$rw)+$rcw+13;						$this->MultiCell($w, $h, 'End', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');						$x += $w; 			$w	=	(4*$rw)+$rcw+7;						$this->MultiCell($w, $h,  'Wks', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M'); 			$x += $w; 			$w	=	(4*$rw)+$rcw+10;						$this->MultiCell($w, $h, 'Spots    Wk', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');						if($headerFlags['showratecard']=='true'){				$x += $w; 				$w	=	(4*$rw)+12;					$this->MultiCell($w, $h, 'RC', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');			}						if($headerFlags['hiderates']=='false'){				$x += $w; 				$w	=	$rw+$rcw+12;					$this->MultiCell($w, $h, 'Rate', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');			}						$x += $w; 			$w	=(5*$rw)+$rcw+10;							$this->MultiCell($w, $h, 'Total   Spots', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');						if($headerFlags['hiderates']=='false'){				$x += $w; 				$w	=	$rcw+18;						$this->MultiCell($w, $h, 'Total', 1, 'C',1,0,$x,$y,true,0,false,true,$h,'M');			}		}				// Page footer		public function Footer() {			$this->SetY(-15);			$this->SetFont('helvetica', 'B', 10);			$this->Cell(0, 10, 'Page '.$this->getAliasNumPage().' of '.$this->getAliasNbPages(), 0, false, 'C', 0, '', 0, false, 'T', 'M');		}					}	?>