<?php			$h = 15;			$y = $start+7;						//CALCULATES IF THERE IS ENOUGH ROOM IN THE PAGE TO DRAW A NEW PROPSOSAL LINE			if(($y+$h)>=$pageLengh){				$y = $start = 46;				$pdf->AddPage();			}						//			$rw 	= ($headerFlags['hiderates']=='true')?1:0;			$rcw 	= ($headerFlags['showratecard']=='true')?0:1;			$x 	= PDF_MARGIN_LEFT;			$w 	= $h;						//FORMATTING DAY PART COLUMN VALUE			$dayPart  = (is_array($line->dayFormat))?implode('-',$line->dayFormat):$line->dayFormat;			$dayPart .= " ".$lineStartTime = str_replace(':00','',date('g:i',strtotime($line->starttime)).substr(date('A',strtotime($line->starttime)),0,1));			$dayPart .= "-".$lineEndTime =  str_replace(array(':00'),array(''),date('g:i',strtotime($line->endtime)).substr(date('A',strtotime($line->endtime)),0,1));			$totalSpots += $line->spots;			$totalAmt 	+= $line->total;			$pdf->SetFont('helvetica', '', 8);									//STATION LOGO COLUMN			if($includelogos == 'true')				$pdf->Image($resJson->networks[$line->stationnum]['100x100'],PDF_MARGIN_LEFT,$y,$w,$h,'','','C',true,300,'M',false, false, 0);			else				$pdf->MultiCell($w+3, $h, $line->callsign, 0, 'C',0,0,PDF_MARGIN_LEFT,$y,true,0,false,true,$h,'M');						$x += $w; 						$w	=	(3*$rw)+$rcw+31;														// DAYPART COLUMN			if($line->premiere != "" || $line->isnew != "")				$pdf->MultiCell($w, $h/2, $dayPart, 0, 'C',0,0,$x,$y,true,0,false,true,$h/2,'B');			else				$pdf->MultiCell($w, $h, $dayPart, 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');									if($line->premiere != "" && $line->showtype == "MV"){ //Movie Premiere				$pdf->SetFont('helvetica', 'I', 8);				$pdf->SetTextColor(230, 0, 0);				$pdf->MultiCell($w, $h/2, "Movie Premiere", 0, 'C',0,0,$x,$y+($h/2),true,0,false,true,$h/2,'T');				$pdf->SetTextColor(0, 0, 0);				$pdf->SetFont('helvetica', '', 8);			}			else if($line->premiere != ""){				// PREMIERES AND FINALES				$pdf->SetFont('helvetica', 'I', 8);				$pdf->SetTextColor(230, 0, 0);				$pdf->MultiCell($w, $h/2, $line->premiere, 0, 'C',0,0,$x,$y+($h/2),true,0,false,true,$h/2,'T');				$pdf->SetTextColor(0, 0, 0);				$pdf->SetFont('helvetica', '', 8);			}			else if($line->isnew != "" && $includenew == 'true'){// NEW IDENTIFIER				$pdf->SetFont('helvetica', 'IB', 8);				$pdf->SetTextColor(0, 100, 0);				$pdf->MultiCell($w, $h/2, "New", 0, 'C',0,0,$x,$y+($h/2),true,0,false,true,$h/2,'T');				$pdf->SetTextColor(0, 0, 0);				$pdf->SetFont('helvetica', '', 8);			}						$pdf->SetFont('helvetica', '', 8);						$formattedTitle  = "<b>".rTrim($line->title,', ')."</b> <span style=\"font-style: italic;color:#3366FF\">{$line->epititle}</span>";			$formattedTitle  = ($includedesc == 'true' && $line->linetype =='Fixed'  && array_key_exists('desc60', $line) )?$formattedTitle.' '.$line->desc60:$formattedTitle;			$formattedTitle  = ($line->live != "")?$formattedTitle." <span style=\"color:#571B7E;font-weight:bold;font-style: italic;\">Live</span>":$formattedTitle;			if(array_key_exists('desc60', $line))				$titlelen = strlen ($line->title.$line->epititle.$line->live.$line->desc60);			else				$titlelen = strlen ($line->title.$line->epititle.$line->live);			$txtlines =  ceil($titlelen / 35) + 1;			$y_pos = $y + ($h / $txtlines) - 2;						$x += $w; 			$w	=	(4*$rw)+(2*$rcw)+50;							$pdf->MultiCell($w, $h, $formattedTitle, 0, 'L',0,0,$x,$y_pos,true,0,true,true,$h,'M');						$pdf->SetFont('helvetica', '', 8);									//START DATE COLUMN			$x += $w; 			$w	=	(4*$rw)+$rcw+13;					$pdf->MultiCell($w, $h, date('m/d/y',strtotime($line->startdate))	, 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');						//END DATE COLUMN			$x += $w; 			$w	=	(4*$rw)+$rcw+13;					$pdf->MultiCell($w, $h, date('m/d/y',strtotime($line->enddate)), 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');						// NUMBER OF WEEKS COLUMN			$x += $w; 			$w	=	(4*$rw)+$rcw+7;					$pdf->MultiCell($w, $h,  $line->weeks, 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');						// SPOTS BY WEEK COLUMN			$x	+= $w; 			$w	=	(4*$rw)+$rcw+10;				$pdf->MultiCell($w, $h, $line->spotsweek, 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');									// RATE COLUMN			if($headerFlags['showratecard'] == 'true'){				$pdf->SetFont('helvetica', 'IB', 8);				$pdf->SetTextColor(0, 100, 0);				$x += $w; 				$w	=	(4*$rw)+12;	$pdf->MultiCell($w, $h, "$".number_format(floatval($line->ratevalue),2), 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');				$pdf->SetFont('helvetica', '', 8);				$pdf->SetTextColor(0, 0, 0);			}						if($headerFlags['hiderates'] == 'false'){				$x += $w; 				$w	=	$rw+$rcw+12;					$pdf->MultiCell($w, $h, "$".number_format(floatval($line->rate),2), 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');			}						// TOTAL SPOTS COLUMN			$x += $w; 			$w	=	(5*$rw)+$rcw+10;					$pdf->MultiCell($w, $h, $line->spots, 0, 'C',0,0,$x,$y,true,0,false,true,$h,'M');			// TOTAL COST COLUMN			if($headerFlags['hiderates'] == 'false'){				$x += $w; 				$w	=	$rcw+18;						$pdf->MultiCell($w, $h, "$".number_format($line->total,2), 0, 'C',0,1,$x,$y,true,0,false,true,$h,'M');			}						$y = $y+$h;			//CALCULATES IF THERE IS ENOUGH ROOM IN THE PAGE TO DRAW A NEW PROPSOSAL LINE						if(($y)>=$pageLengh)	{				$y = $start = 46;				$pdf->AddPage();			}			$pdf->SetLineStyle(array('width' => 0.5, 'cap' => 'butt', 'join' => 'miter', 'dash' => 0, 'color' => array(192, 192, 192)));			$pdf->SetY($y);			$pdf->Cell(0, 0, '', 'T', 0, 'C');						$start = $start+$h+2; ?>