<?phpfunction createFirstSlide(PHPPowerPoint $objPHPPowerPoint, $arrUser, $proposal){	// Create slide	$slide = $objPHPPowerPoint->createSlide();    $slide->createDrawingShape()          ->setName('Background')          ->setDescription('Background')          ->setPath(getcwd().'/ppt.sr/corps/'.$arrUser["corporationId"].'/ini.png')          ->setWidth(955)          ->setHeight(720)          ->setOffsetX(0)          ->setOffsetY(0);		  	if($arrUser['corporationId'] != 15  && $arrUser['corporationId'] != 46){		$x_pos 		= 0;		$y_pos 		= 200;		$x_align 	= PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER;		$x_width 	= 950;		$font_size  = 36;	}	else{		$x_align 	= PHPPowerPoint_Style_Alignment::HORIZONTAL_LEFT;		$y_pos 		= 250;		$x_pos 		= 490;		$x_width 	= 455;		$font_size  = 40;	}	$font_subtitle  = 16;			if($arrUser['corporationId'] == 5 || $arrUser['corporationId'] == 30){			$colorCode ='0000629B';	}	elseif($arrUser['corporationId'] == 15  || $arrUser['corporationId'] == 46){		$colorCode ='00003057';				$pslNameLen = strlen($proposal['name']);				if($pslNameLen > 11 && $pslNameLen < 16){			$font_size  = 30;		}		elseif($pslNameLen > 15 && $pslNameLen < 21){			$font_size  = 24;		}		elseif($pslNameLen > 20 && $pslNameLen < 24){			$font_size  = 20;		}		elseif($pslNameLen > 23 && $pslNameLen < 31){			$font_size  = 16;		}		elseif($pslNameLen > 30 && $pslNameLen < 34){			$font_size  = 30;			$y_pos 		= 210;		}		elseif($pslNameLen > 33 && $pslNameLen < 41){			$font_size  = 24;			$y_pos 		= 210;		}		elseif($pslNameLen > 40 && $pslNameLen < 49){			$font_size  = 20;			$y_pos 		= 210;		}		elseif($pslNameLen > 48){			$font_size  = 16;			$y_pos 		= 230;			$font_subtitle  = 14;		}	}	else{		$colorCode ='44444444';	}	//PROPOSAL NAME	$shape = $slide->createRichTextShape();	$shape->setHeight(150);	$shape->setWidth($x_width);	$shape->setOffsetX($x_pos);	$shape->setOffsetY($y_pos);	$shape->getActiveParagraph()->getAlignment()->setHorizontal( $x_align );		$textRun = $shape->createTextRun($proposal['name']);	$textRun->getFont()->setBold(true);	$textRun->getFont()->setSize($font_size);	$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	if($arrUser['corporationId'] != 15  && $arrUser['corporationId'] != 46){		$shape->createBreak();		}	$shape->createBreak();		$colorCode ='33333333'; 		if($arrUser['corporationId'] == 15 || $arrUser['corporationId'] == 46){		$colorCode ='00003057';	}	$textRun = $shape->createTextRun('Schedule Dates: '. $proposal['startdate'].' - '.$proposal['enddate']);	$textRun->getFont()->setSize($font_subtitle);	$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	if($arrUser['corporationId'] == 15  || $arrUser['corporationId'] == 46){		$shape->createBreak();		if($font_subtitle < 16){		$shape->createBreak();					}		}	else{		$shape->createBreak();			$shape->createBreak();					}	$textRun = $shape->createTextRun('Presented by:');	$textRun->getFont()->setSize($font_subtitle);		if($arrUser['corporationId'] == 15 || $arrUser['corporationId'] == 46){		$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	}	if($arrUser['corporationId'] != 15  && $arrUser['corporationId'] != 46){		$x_pos = 325;		$y_pos = 420;	}	else{		$colorCode ='000077BC';				$x_pos = 490;		$y_pos = 380;	}	$shape = $slide->createRichTextShape();	$shape->setHeight(90);	$shape->setWidth(300);	$shape->setOffsetX($x_pos);	$shape->setOffsetY($y_pos);	$shape->getActiveParagraph()->getAlignment()->setHorizontal( $x_align );				$textRun = $shape->createTextRun($arrUser['firstName'].' '.$arrUser['lastName']);	$textRun->getFont()->setBold(true);	$textRun->getFont()->setSize(20);	$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	$shape->createBreak();	$textRun = $shape->createTextRun($arrUser['address']);	if($arrUser['corporationId'] == 15  || $arrUser['corporationId'] == 46){		$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	}	$shape->createBreak(); 	$textRun = $shape->createTextRun(trim($arrUser['city'].', '.$arrUser['state'].' '.$arrUser['zip']));	if($arrUser['corporationId'] == 15  || $arrUser['corporationId'] == 46){		$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	}	$shape->createBreak();	if($arrUser['phone'] != ''){		$textRun = $shape->createTextRun($arrUser['phone']);			if($arrUser['corporationId'] == 15  || $arrUser['corporationId'] == 46){			$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );		}	}    return $slide;}?>