<?php function createColumnHeader(&$row,$w){			$row->getFill()->setFillType(PHPPowerPoint_Style_Fill::FILL_GRADIENT_LINEAR)	               ->setRotation(90)	               ->setStartColor(new PHPPowerPoint_Style_Color('CCCCCCCC'))	               ->setEndColor(new PHPPowerPoint_Style_Color('CCCCCCCC'));		$row->setHeight(20);			$cell = $row->nextCell();	$cell->setWidth(80);	$cell->createTextRun('Net')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER);			$cell = $row->nextCell();	$cell->setWidth(350);	$cell->createTextRun('Program')->getFont()->setBold(true)->setSize(12);	$cell = $row->nextCell();	$cell->setWidth(120);	$cell->createTextRun('Daypart')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_LEFT);		$cell = $row->nextCell();	$cell->setWidth($w);		$cell->createTextRun('Dates')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER);		$cell = $row->nextCell();	$cell->setWidth(50);	$cell->createTextRun('Wks')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER);			$cell = $row->nextCell();	$cell->setWidth(75);	$cell->createTextRun('Rate')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_RIGHT);		$cell = $row->nextCell();	$cell->setWidth(60);	$cell->createTextRun('Spots')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER);		$cell = $row->nextCell();	$cell->setWidth(90);	$cell->createTextRun('Total')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER);	foreach ($row->getCells() as $cell) {		$cell->getBorders()->getTop()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);		$cell->getBorders()->getRight()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);		$cell->getBorders()->getBottom()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);		$cell->getBorders()->getLeft()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);		$cell->getActiveParagraph()->getAlignment()->setVertical(PHPPowerPoint_Style_Alignment::VERTICAL_CENTER);	}	return $row;}?>