<?php function createProposalName(&$currentSlide, $proposalname){	$shape = $currentSlide->createTableShape(1);	$shape->setHeight(80);	$shape->setWidth(645);	$shape->setOffsetX(5);	$shape->setOffsetY(20);	$row = $shape->createRow();	$row->setHeight(50);		$cell = $row->nextCell();	$cell->setWidth(645);	$cell->createTextRun($proposalname['name'])->getFont()->setBold(true)->setSize(15)->setColor( new PHPPowerPoint_Style_Color( 'FFFFFFFF' ) );	$cell->getBorders()->getTop()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);	$cell->getBorders()->getRight()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);	$cell->getBorders()->getLeft()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);				//$cell->getBorders()->getBottom()->setLineStyle(PHPPowerPoint_Style_Border::LINE_SINGLE);	$cell->getBorders()->getBottom()->setLineStyle(PHPPowerPoint_Style_Border::LINE_NONE);		//$cell->getBorders()->getBottom()->setColor(new PHPPowerPoint_Style_Color('66666666'));	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER);			$cell->getActiveParagraph()->getAlignment()->setVertical(PHPPowerPoint_Style_Alignment::VERTICAL_CENTER);	return $shape;}?>