<?phpfunction createFirstSlide(PHPPowerPoint $objPHPPowerPoint, $arrUser, $proposal,$arrTax){	// Create slide	$slide = $objPHPPowerPoint->createSlide();    $slide->createDrawingShape()          ->setName('Background')          ->setDescription('Background')          ->setPath(getcwd().'/ppt/corps/'.$arrUser["corporationId"].'/ini.png')          ->setWidth(955)          ->setHeight(720)          ->setOffsetX(0)          ->setOffsetY(0);		  	if($arrUser['corporationId'] != 15){		$x_pos 		= 0;		$y_pos 		= 200;		$x_align 	= PHPPowerPoint_Style_Alignment::HORIZONTAL_CENTER;		$x_width 	= 950;		$font_size  = 36;	}	else{		$x_align 	= PHPPowerPoint_Style_Alignment::HORIZONTAL_LEFT;		$y_pos 		= 250;		$x_pos 		= 450;		$x_width 	= 500;		if(strlen($proposal['name']) < 21){			$font_size  = 40;					}		elseif(strlen($proposal['name']) < 31){			$font_size  = 28;					}		else{			$font_size  = 20;		}	}		if($arrUser['corporationId'] == 5 || $arrUser['corporationId'] == 30){			$colorCode ='0000629B';	}	elseif($arrUser['corporationId'] == 15){		$colorCode ='00003057';			}	else{		$colorCode ='44444444';	}	$taxIds ='';	$i = 1;	foreach ($arrTax as $value){		if(count($arrTax) == $i){			$taxIds = $taxIds.''.$value['taxLabel'].' '.$value['taxId'];		}else{			$taxIds = $value['taxLabel'].' '.$value['taxId'].' - '.$taxIds;		}		$i++;	}		$taxCode = $slide->createRichTextShape();	$taxCode->setHeight(50);	$taxCode->setWidth($x_width);	$taxCode->setOffsetX($x_pos);	$taxCode->setOffsetY($y_pos-90);	$taxCode->getActiveParagraph()->getAlignment()->setHorizontal( $x_align );		$taxRun = $taxCode->createTextRun($taxIds);	$taxRun->getFont()->setSize(10);	$taxRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	//PROPOSAL NAME	$shape = $slide->createRichTextShape();	$shape->setHeight(150);	$shape->setWidth($x_width);	$shape->setOffsetX($x_pos);	$shape->setOffsetY($y_pos);	$shape->getActiveParagraph()->getAlignment()->setHorizontal( $x_align );		$textRun = $shape->createTextRun($proposal['name']);	$textRun->getFont()->setBold(true);	$textRun->getFont()->setSize($font_size);	$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	if($arrUser['corporationId'] != 15){		$shape->createBreak();		}	$shape->createBreak();		$colorCode ='33333333'; 		if($arrUser['corporationId'] == 15){		$colorCode ='00003057';	}	$textRun = $shape->createTextRun('Schedule Dates: '. $proposal['startdate'].' - '.$proposal['enddate']);	$textRun->getFont()->setSize(18);	$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	if($arrUser['corporationId'] == 15){		$shape->createBreak();		}	else{		$shape->createBreak();			$shape->createBreak();					}	$textRun = $shape->createTextRun('Presented by:');	$textRun->getFont()->setSize(18);		if($arrUser['corporationId'] == 15){		$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	}	if($arrUser['corporationId'] != 15){		$x_pos = 325;		$y_pos = 420;	}	else{		$colorCode ='000077BC';				$x_pos = 450;		$y_pos = 390;	}	$shape = $slide->createRichTextShape();	$shape->setHeight(90);	$shape->setWidth(300);	$shape->setOffsetX($x_pos);	$shape->setOffsetY($y_pos);	$shape->getActiveParagraph()->getAlignment()->setHorizontal( $x_align );				$textRun = $shape->createTextRun($arrUser['firstName'].' '.$arrUser['lastName']);	$textRun->getFont()->setBold(true);	$textRun->getFont()->setSize(20);	$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	$shape->createBreak();	$textRun = $shape->createTextRun($arrUser['address']);	if($arrUser['corporationId'] == 15){		$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	}	$shape->createBreak(); 	$textRun = $shape->createTextRun($arrUser['city'].', '.$arrUser['state'].' '.$arrUser['zip']);	if($arrUser['corporationId'] == 15){		$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );	}	$shape->createBreak();	if($arrUser['phone'] != ''){		$textRun = $shape->createTextRun($arrUser['phone']);			if($arrUser['corporationId'] == 15){			$textRun->getFont()->setColor( new PHPPowerPoint_Style_Color( $colorCode ) );		}	}    return $slide;}?>