<?phpfunction createZoneLabel(&$currentSlide, $zonename,$corpid){	$shape = $currentSlide->createTableShape(1);	$shape->setHeight(25);	$shape->setWidth(950);	$shape->setOffsetX(25);	if($corpid != 15 && $corpid != 5){		$shape->setOffsetY(100);	}	else{		$shape->setOffsetY(60);			}				//ADD Zone Info	//echo date('H:i:s') . " Add COLUMN HEADER\n";	$row = $shape->createRow();	$row->setHeight(24);	$cell = $row->nextCell();	$cell->setWidth(915);	if($corpid != 4 && $corpid != 14 && $corpid != 15 && $corpid != 20 && $corpid != 30){		$cell->createTextRun('Zone: '. $zonename)			  ->getFont()			  ->setBold(true)			  ->setSize(12)			  ->setColor( new PHPPowerPoint_Style_Color( '33333333' ) );	}	elseif($corpid == 15){		// CHARTER COLOR		$cell->createTextRun('Zone: '. $zonename)			  ->getFont()			  ->setBold(true)			  ->setSize(14)			  ->setColor( new PHPPowerPoint_Style_Color( '0000629B' ) );		}	else{		// CHARTER COLOR		$cell->createTextRun('Zone: '. $zonename)			  ->getFont()			  ->setBold(true)			  ->setSize(12)			  ->setColor( new PHPPowerPoint_Style_Color( '0000629B' ) );		}	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_RIGHT);	// remove borders	$nobdr = noBorder($row); 			return $shape;}?>