<?phpfunction createZoneTotal($row,$spots,$total){	$row->setHeight(20);		$cell = $row->nextCell();	$cell = $row->nextCell();	$cell = $row->nextCell();	$cell = $row->nextCell();	$cell = $row->nextCell();		$cell = $row->nextCell();	$cell->createTextRun('Total')->getFont()->setBold(true)->setSize(12);	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_RIGHT);			$lineBorder = noBorder($row);		$cell = $row->nextCell();	$cell->createTextRun($spots)->getFont()->setBold(true)->setSize(12);	$cell->getBorders()->getBottom()->setLineWidth(1)->setLineStyle(PHPPowerPoint_Style_Border::LINE_SINGLE);	$cell->getBorders()->getBottom()->setColor(new PHPPowerPoint_Style_Color('33333333'));	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_RIGHT);		$cell = $row->nextCell();	$cell->createTextRun(money_format('$%i',$total))->getFont()->setBold(true)->setSize(12);	$cell->getBorders()->getBottom()->setLineWidth(1)->setLineStyle(PHPPowerPoint_Style_Border::LINE_SINGLE);	$cell->getBorders()->getBottom()->setColor(new PHPPowerPoint_Style_Color('33333333'));	$cell->getActiveParagraph()->getAlignment()->setHorizontal(PHPPowerPoint_Style_Alignment::HORIZONTAL_RIGHT);	return $row;}?>