<?php		class Station{	protected $con;	//build the basic information about the logged in user	public function __construct($con){		$this->con = $con;	}			//--- USER ZONES --->	public function getInfo($networkid,$zoneid){			$sql = "select 			TimeZone.mysql as mysqlswitch, 				TimeZone.abbreviation, 				Network.callsign, 				Network.networkid				from 			Network				inner join 		ZoneNetwork 				on				Network.networkid = ZoneNetwork.networkid				inner join 		Zone				on				ZoneNetwork.zoneid = Zone.id				inner join 		TimeZone				on				Zone.timezoneid = TimeZone.id				where			Network.networkid = $networkid				and				Zone.id = $zoneid";						$r		=mysqli_query($this->con,$sql);				$result = mysqli_fetch_assoc($r);		return $result;	}	}?>