<div class="ezrating-wrapper divRow">	<div class="ezrating-panel-title"><i class="fa fa-calendar fa-lg"></i> Proposal Flight Weeks</div>	<br style="clear:both;">			<div id="flightdates"></div>	<br style="clear:both;"><br></div><p>	<center>		<label class="label" for="showWeeksOff" id="lbl-show-weeks-of">Show Weeks Off</label>		<input type="checkbox" id="showWeeksOff">		<button id="flightcheckall" class="btn-blue">Check All</button>		<button id="flightapply" onclick="flightUpdate();" class="btn-green">Apply Changes</button>	</center></p><script>populateFlightCalendar();</script><script>	if(userSettings.showWeeksOff){		$('#showWeeksOff').prop('checked',true);			}		else{		$('#showWeeksOff').prop('checked',false);	}		$('#flightcheckall,#flightapply,#showWeeksOff').button();			//check all	$('#flightcheckall').click(function() {	    $('#flightdates').find(':checkbox').each(function() {	        if (!$(this).is(':checked')) {	            $(this).prop('checked', true).change();	        }	    });	});		$('input:checkbox.proposalWeek').on('change',function(){		$('#showWeeksOff,#flightapply').prop('disabled',false);				if($('input:checkbox.proposalWeek:checked').length < 1){			$('#showWeeksOff,#flightapply').prop('disabled',true);		}		$('#showWeeksOff,#flightapply').button('refresh');	});</script>