<div style="margin-left: auto; margin-right: auto; width: 80%; padding-top:5px;"><center>	<div style="padding:2px;"  id="popUpSpots">		<label for="edit-line-spots" style="width:90px; display:inline-block;"> Spots by Week:</label>		<input class="input-half rounded-corners"  onkeypress="return isValidSpot(event);" type="text" id="edit-line-spots" value="1" />		<button class="btn-blue" onclick="applySpotsOverlay()" id="applySpotsBtn">Apply Spots</button>	</div><div style="padding:2px;"><label for="edit-line-rate" style="width:90px; display:inline-block;">Rate: $</label><input class="input-half rounded-corners"  onkeypress="return isNumberKey(event, this.value);" type="text" id="edit-line-rate" value="0" /><button class="btn-blue" onclick="applyRateOverlay();" id="applyRateBtn">Apply Rates</button></div><p id="btn-apply-rates" align="center"><span class="btn-apply-rates-disc" style="display:none;">	Disc %: <input maxlength="2" class="input-half rounded-corners" onkeypress="return isNumberKey(event, this.value);"  type="text" id="edit-line-discount" value=0 style="width:25px;"></span><button class="btn-green" onclick="applyRateCardValues();" id="applyRcBtn">	<i class="fa fa-money fa-lg"></i> Apply Ratecard </button><span class="btn-apply-rates-disc" style="display:none;">	<button class="btn-red" onclick="$('#edit-line-discount').val(0)" id="reset-rc-btn">Reset</button></span></p><div id="btn-apply-rates-grey" style="display:none; margin-left: auto; margin-right: auto; width: 95%;">	<br>	<!-- div class="ui-state-highlight ui-corner-all" style="padding: 0 .7em;">	  <p>	    <span class="ui-icon ui-icon-alert" style="float: left; margin-right: .3em;"></span>	    Ratecards are not authorized for your market or zone.	  </p>	</div --></div><br><div style="height: 40px; width:100%;"><span id="updateLinesFlag"> &nbsp; <i class="fa fa-spinner fa-spin fa-fw fa-3x" aria-hidden="true" style="color: #444; opacity: .5"></i></span></div></center></div><script>	$('#updateLinesFlag').hide();		$('#btn-apply-rates-grey').ready(function(){		if(ratecardZone != false){			$('#btn-apply-rates').css("display", "block");			$('#btn-apply-rates-grey').hide();			}		else{			$('#btn-apply-rates').hide();			$('#btn-apply-rates-grey').css("display", "block");			}		});			$('#applySpotsBtn,#applyRateBtn,#applyRcBtn,#reset-rc-btn').button();		if(corpid == 16){		$('.btn-apply-rates-disc').css("display", "inline");	}		var selectedLines  = datagridProposal.selectedRowsByType();		if(toggleEditionOfSpots(selectedLines.ByDay,datagridProposal.selectedRows().length)){		$('#popUpSpots').addClass('redBackground').find('input').prop('disabled', true);		$('#applySpotsBtn').prop('disabled', true).button("refresh");	}			function applyRateCardValues(){		openApiCallFlag();		setTimeout(function(){ 			needSaving = true;			datagridProposal.applyRateCard($('#edit-line-discount').val());			},200);	};	function applyRateOverlay(){		openApiCallFlag();		setTimeout(function(){ 			needSaving = true;			datagridProposal.updateRateFromOverlay('rate');			},200);	};	function applySpotsOverlay(){		openApiCallFlag();		setTimeout(function(){ 			needSaving = true;			datagridProposal.updateSpotsFromOverlay();			},200);	};	      	</script>