<center>	<br />	Spot Length: 	<span id="bookend-mode">		<input type="radio" id="defaut-15" name="spotlen-mode-option" value="15"/><label for="defaut-15">:15</label>		<input type="radio" id="defaut" name="spotlen-mode-option" value="30" checked="checked"/><label for="defaut">:30</label>		<input type="radio" id="defaut-60" name="spotlen-mode-option" value="60"/><label for="defaut-60">:60</label>		<!-- input type="radio" id="bookend-15" name="spotlen-mode-option" value="B30"/><label for="bookend-15">:15/:15</label -->	</span>	<p>		<br><br>		<button id="spot-length-btn" class="btn-green" onclick="applySpotLength(); $('#dialog-window').dialog('destroy');">Apply</button>	</p></center><script>	$('#bookend-mode').buttonset();	$('#spot-length-btn').button();		function applySpotLength(){		var spLen 	= $("input[name='spotlen-mode-option']:checked"). val();		var rows 	= datagridProposal.selectedRowsData();		var idx 	= 0;				var data = {};		data.spotLength = spLen;		data.lineIds = [];		$.each(rows,function(i,r){			datagridProposal.updateSpotLen(r.id,spLen);			data.lineIds.push(r.id);		});		$.ajax({			type:'post',			url: apiUrl+"proposal/line/editspotlength",			dataType:"json",			headers:{"Api-Key":apiKey,"User":userid},			processData: false,			contentType: 'application/json',			data: JSON.stringify(data),			success:function(resp){				//console.log(resp);			}		});			}</script>