<script type="text/javascript">	loadUserClients();		function loadUserClients(){		var url =  apiUrl+'clientmanager/list';		    $.ajax({	        type:'get',	        url: url,					dataType:"json",			headers:{"Api-Key":apiKey,"User":userid},			processData: false,			contentType: 'application/json',						        success:function(data){				datagridClients = new DatagridClients();				datagridClients.populateDataGrid(data.clients);		    }	    });	}	function clearClientForm(){		clientid = 0;		$('#client-company').val('');		$('#client-fname').val('');		$('#client-lname').val('');		$('#client-email').val('');		$('#client-a1phone').val('');		$('#client-a2phone').val('');		$('#client-a3phone').val('');		$('#client-a1ext').val('');	}	function loadClientContent(){		var rows = datagridClients.getSelectedRows();		if(rows.length > 1 || rows.length == 0){			return;		}		$('#client-company').val(rows[0].company);		$('#client-fname').val(rows[0].firstName);		$('#client-lname').val(rows[0].lastName);		$('#client-email').val(rows[0].email);		$('#client-a1phone').val(rows[0].a1phone);		$('#client-a2phone').val(rows[0].a2phone);		$('#client-a3phone').val(rows[0].a3phone);		$('#client-a1ext').val(rows[0].a1ext);				$('#download-panel-client').html(rows[0].firstName + " " + rows[0].lastName + " (" + rows[0].company + ")");		$("#download-remove-client").css('display', 'inline');		clientid = rows[0].id;		$('#email-company').val('');		$('#email-first-name').val('');		$('#email-last-name').val('');		$('#email-email1').val('');	}	function loadClientEmail(){		var rows = datagridClients.getSelectedRows();		if(rows.length > 1 || rows.length == 0){			return;		}		$('#client-company, #email-company').val(rows[0].company);		$('#client-fname, #email-first-name').val(rows[0].firstName);		$('#client-lname, #email-last-name').val(rows[0].lastName);		$('#client-email, #email-email1').val(rows[0].email);		$('#client-a1phone').val(rows[0].a1phone);		$('#client-a2phone').val(rows[0].a2phone);		$('#client-a3phone').val(rows[0].a3phone);		$('#client-a1ext').val(rows[0].a1ext);	}	function clientSaveChanges(){		var company = String($('#client-company').val()).trim();		var fname 	= String($('#client-fname').val()).trim();		var lname 	= String($('#client-lname').val()).trim();		var email 	= String($('#client-email').val()).trim();		var a1phone = String($('#client-a1phone').val()).trim();		var a2phone = String($('#client-a2phone').val()).trim();		var a3phone = String($('#client-a3phone').val()).trim();		var a1ext 	= $('#client-a1ext').val();				if(company.length < 1){			alert('Company is a required field.');			return;		}		if(fname.length < 1){			alert('First Name is a required field.');			return;		}				if(lname.length < 1){			alert('Last Name is a required field.');			return;		}			if(email.length < 1){			alert('Email is a required field.');			return;		}				if(!IsEmail(email)){			alert('Invalid Email address.');			return;					}		if(a1phone.length < 1 || a2phone.length < 1 || a1phone.length <1){			alert('Phone is a required field.');			return;		}					$('#download-panel-client').html(fname + " " + lname + " (" + company + ")");				var url;		clientInfo 				= {};		clientInfo.clientId		= clientid;		clientInfo.company		= company;		clientInfo.firstName	= fname;		clientInfo.lastName		= lname;		clientInfo.email		= email;		clientInfo.a1Phone		= a1phone;		clientInfo.a2Phone		= a2phone		clientInfo.a3Phone		= a3phone		clientInfo.a1Ext		= a1ext						if(datagridClients.getSelectedRows().length === 0){			url =  apiUrl+'clientmanager/new';		    $.ajax({		        type:'post',		        url: url,						dataType:"json",				headers:{"Api-Key":apiKey,"User":userid},				processData: false,				data: JSON.stringify(clientInfo),				contentType: 'application/json',								        success:function(data){					datagridClients = new DatagridClients();					loadUserClients();			    }		    });			}	    else{			url =  apiUrl+'clientmanager/edit';		    $.ajax({		        type:'post',		        url: url,						dataType:"json",				headers:{"Api-Key":apiKey,"User":userid},				processData: false,				data: JSON.stringify(clientInfo),				contentType: 'application/json',								        success:function(data){					datagridClients = new DatagridClients();					loadUserClients();			    }		    });			    	    }	}	$("button").button();</script><div class="gridwrapper" style="width:450px;float:left;">	<div style="height:400px;width:450px;" id="datagrid-clients"></div></div><div class="gridwrapper" style="height:370px;padding:15px;float:left;margin-left:10px;">		<table cellpadding="3">				<tr>					<td nowrap="nowrap" align="right">Company:</td>					<td><input class="input-q rounded-corners" id="client-company" type="text" maxlength="100"/></td>				</tr>				<tr>					<td nowrap="nowrap" align="right">First Name:</td>					<td><input class="input-q rounded-corners" id="client-fname" type="text" maxlength="50"/></td>				</tr>				<tr>					<td nowrap="nowrap" align="right">Last Name:</td>					<td><input class="input-q rounded-corners" id="client-lname" type="text" maxlength="50"/></td>				</tr>				<tr>					<td nowrap="nowrap" align="right">Email Address:</td>					<td><input class="input-q rounded-corners" id="client-email" type="text" maxlength="100"/></td>				</tr>				<tr>					<td nowrap="nowrap" align="right">Phone:</td>					<td>						<input class="input-q rounded-corners" style="width:30px" id="client-a1phone" type="text" maxlength="5"/>						<input class="input-q rounded-corners" style="width:30px" id="client-a2phone" type="text" maxlength="5"/>						<input class="input-q rounded-corners" style="width:30px" id="client-a3phone" type="text" maxlength="5"/>					</td>				</tr>				<tr>					<td nowrap="nowrap" align="right">Phone Ext:</td>					<td>						<input class="input-q rounded-corners" style="width:30px" id="client-a1ext" type="text" maxlength="8"/>					</td>				</tr>											<tr>					<td colspan="2">						<center><p><button id="client-new" onclick="clearClientForm();" class="btn-blue">Clear Form</button></p></center>					</td>				</tr>		</table></div><br style="clear:both;"><p></p><center>	<button id="client-save" onclick="clientSaveChanges();" class="btn-green">Save Changes</button>	<button class="btn-red" onclick='closeAllDialogs();'>Close</button>	<!--	<button class="btn-red">Archive Items</button>--></center>