<div class="gridwrapper" style="width:96%; height:auto; overflow: hidden;" id="daysOfWeekWrapper">	 	<table class="spotsCalendar"  id="spotByDayContainer">		<thead>			<tr><th>Day</th><th>Date</th><th width="20">Spots</th></tr>		</thead>		<tbody>			<tr>				<td class="wDates"><b>M</b></td>				<td class="spotsMonday wDates"></td>				<td align="center"><input type="text" size="2" maxlength="2" id="spotsMonday" class="spotsbyweekinline input-quarter rounded-corners"></td>			</tr>			<tr>				<td class="wDates"><b>Tu</b></td>				<td class="spotsTuesday wDates"></td>				<td align="center"><input type="text" size="2" maxlength="2" id="spotsTuesday" class="spotsbyweekinline input-quarter rounded-corners"></td>			</tr>			<tr>				<td class="wDates"><b>W</b></td>				<td class="spotsWednesday wDates"></td>				<td align="center"><input type="text" size="2" maxlength="2" id="spotsWednesday" class="spotsbyweekinline input-quarter rounded-corners"></td>			</tr>			<tr>				<td class="wDates"><b>Th</b></td>				<td class="spotsThursday wDates"></td>				<td align="center"><input type="text" size="2" maxlength="2" id="spotsThursday" class="spotsbyweekinline input-quarter rounded-corners"></td>				</tr>			<tr>				<td class="wDates"><b>Fr</b></td>				<td class="spotsFriday wDates"></td>				<td align="center"><input type="text" size="2" maxlength="2" id="spotsFriday" class="spotsbyweekinline input-quarter rounded-corners"></td>			</tr>			<tr>				<td class="wDates"><b>Sa</b></td>				<td class="spotsSaturday wDates"></td>				<td align="center"><input type="text" size="2" maxlength="2" id="spotsSaturday" class="spotsbyweekinline input-quarter rounded-corners"></td>			</tr>			<tr>				<td class="wDates"><b>Su</b></td>				<td class="spotsSunday wDates"></td>				<td align="center"><input type="text" size="2" maxlength="2" id="spotsSunday" class="spotsbyweekinline input-quarter rounded-corners"></td>			</tr>		</tbody>	</table>	</div><br /><br /><div>	<center>		<buton class="btn-green" id="saveSpotsByWeek">Save</buton>		<buton class="btn-red" id="cancelLineByDateBtn" onclick="closeSpotsBydayModal();">Cancel</buton>	</center></div><script>		$('#createLineByDayBtn,#cancelLineByDateBtn').button();		$('.btn-green,.btn-red').button();		var dData = $("#dialog-spots-by-day").data().dialogData;		editSpotsInWeek(dData.week,dData.row);	</script>