<div class="gridwrapper" style="width:96%; height:auto; overflow: hidden;" id="daysOfWeekWrapper">	<table class="spotsCalendar"  id="spotByDayContainer">		<thead><!-- th>M</th><th>Tu</th><th>W</th><th>Th</th><th>F</th><th>Sa</th><th>Su</th -->			<tr><th>Day</th><th width="40%">Spots</th></tr>		</thead>		<tbody>			<tr>				<td class="wDates"><b>Monday</b></td>				<td align="center">					<input size="2" maxlength="2" id="spotsMonday" type="text" value="0" class="spotsbyweek input-80 rounded-corners">				</td>			</tr>			<tr>				<td class="wDates"><b>Tuesday</b></td>				<td align="center">							<input size="2" maxlength="2" id="spotsTuesday" type="text" value="0" class="spotsbyweek input-80 rounded-corners">				</td>			</tr>			<tr>				<td class="wDates"><b>Wednesday</b></td>				<td align="center">					<input size="2" maxlength="2" id="spotsWednesday" type="text" value="0" class="spotsbyweek input-80 rounded-corners">				</td>			</tr>			<tr>				<td class="wDates"><b>Thursday</b></td>				<td align="center">					<input size="2" maxlength="2" id="spotsThursday" type="text" value="0" class="spotsbyweek input-80 rounded-corners">				</td>			</tr>			<tr>				<td class="wDates"><b>Friday</b></td>				<td align="center">					<input size="2" maxlength="2" id="spotsFriday" type="text" value="0" class="spotsbyweek input-80 rounded-corners">				</td>			</tr>			<tr>				<td class="wDates"><b>Saturday</b></td>				<td align="center">					<input size="2" maxlength="2" id="spotsSaturday" type="text" value="0" class="spotsbyweek input-80 rounded-corners">				</td>			</tr>			<tr>				<td class="wDates"><b>Sunday</b></td>				<td align="center">					<input size="2" maxlength="2" id="spotsSunday" type="text" value="0" class="spotsbyweek input-80 rounded-corners">				</td>			</tr>		</tbody>	</table></div><br /><br /><div>	<center>		<buton class="btn-green" id="createLineByDayBtn"><i class="fa fa-plus-circle"></i> Create Line</buton>		<buton class="btn-blue" id="updateLineByDayBtn" style="display: none;" onclick="datagridProposal.updateSelectedRotators(1);needSaving=true;"><i class="fa fa-pencil"></i> Update Line</buton>		<buton class="btn-red"   id="cancelLineByDateBtn" onclick="closeAllDialogs();">Cancel</buton>	</center></div><script>	$('#createLineByDayBtn,#updateLineByDayBtn,#cancelLineByDateBtn').button();	allocateSpotsByDay();	$('.spotsbyweek').on('input',function(){		manualSpotAllocation = true;	});	</script>