<div class="" style="width:100%; height:350px; overflow:hidden;">	<form method="post" id="xmlInfo" action="">			<div id="xmlTabs" style="height:290px">					<ul class="tab">				<li><a href="#seller">Seller</a></li>				<li><a href="#buyer">Buyer</a></li>				<li><a href="#demographics">Demographics</a></li>			</ul>				<div id="seller">					<div class="spacing"></div>					<div class="ftext">Name:</div>				<input type="text" id="seller_name" class="finput" required="true">								<div class="spacing"></div>								<div class="ftext">Company:</div>				<input type="text" id="seller_company"class="finput" required="true">					<div class="spacing"></div>					<div class="ftext">Office:</div>				<input type="text" id="seller_office" class="finput" required="true">					<div class="spacing"></div>					<div class="ftext">Phone:</div>								<input type="text" id="seller_phone1" class="finput" style="width:76px; margin-right:3px" maxlength="3" required="true">				<input type="text" id="seller_phone2" class="finput" style="width:76px; margin-right:3px" maxlength="3" required="true">				<input type="text" id="seller_phone3" class="finput" style="width:76px; margin-right:3px" maxlength="4" required="true">					<div class="spacing"></div>								<div class="ftext">Email:</div>				<input type="email" id="seller_email" class="finput" required="true">					<div class="spacing"></div>						</div>				<div id="buyer">						<div class="spacing"></div>					<div class="ftext">Name:</div>				<input type="text" id="bname" class="finput" value="Please enter buyer name" required="true">								<div class="spacing"></div>								<div class="ftext">Company:</div>				<input type="text" id="bcompany" class="finput" required="true">					<div class="spacing"></div>					<div class="ftext">Office:</div>				<input type="text" id="boffice" class="finput" required="true">					<div class="spacing"></div>					<div class="ftext">Advertiser:</div>				<input type="text" id="badvertiser" class="finput" value="Advertiser" required="true">					<div class="spacing"></div>					<div class="ftext">Product:</div>				<input type="text" id="bproduct" class="finput" value="1QFull" required="true">					<div class="spacing"></div>			</div>					<div id="demographics">						<div class="spacing"></div>								<div class="ftext">Type :</div>				<select  class="fselect" id="dtype" name="dtype">					<option value="Rating" selected="selected">Rating</option>						<option value="Impression">Impression</option>					</select>					<div class="spacing"></div>								<div class="ftext">Group :</div>				<select  class="fselect" id="group" name="group">					<option value="Adults">Adults</option>						<option value="Men">Men</option>					<option value="Women">Women</option>					<option value="Teens">Teens</option>					<option value="Children">Children</option>					<option value="Persons">Persons</option>					<option value="Homes">Homes</option>					<option value="WWomen">WWomen</option>				</select>								<div class="spacing"></div>								<div class="ftext">Age From :</div>								<select  class="fselect" id="agefrom" name="agefrom">					<option value="0">0</option>						<option value="2">2</option>					<option value="6">6</option>					<option value="12">12</option>					<option value="18">18</option>					<option value="21">21</option>					<option value="25" selected="selected">25</option>					<option value="35">35</option>					<option value="45">45</option>					<option value="50">50</option>					<option value="55">55</option>					<option value="65">65</option>				</select>								<div class="spacing"></div>								<div class="ftext">Age To :</div>				<select  class="fselect" id="ageto" name="ageto">					<option value="5">5</option>						<option value="11">11</option>					<option value="17">17</option>					<option value="20">20</option>					<option value="24">24</option>					<option value="34">34</option>					<option value="44">44</option>					<option value="49">49</option>					<option value="54" selected="selected">54</option>					<option value="64">64</option>					<option value="99">99</option>				</select>							</div>				</div>		<center>				<div class="panel radius" style="display: none; font-size: 11px; background: #fad0d0;  border: 1px solid #f6abab; color: #555;" id="errorMessage"></div>				<div id style="width: 100%; height: 80px; line-height: 80px;">				<button id="downloadXML"type="submit" class="btn-green"  style="font-size:larger;"> Download </button>				<span id="xmlWaitMsg" style="display: none;"> <img src="i/ajax.gif" width="20px"> Downloading please wait ...</span>			</div>		</center>	</form></div><script>	$("#xmlTabs").tabs({ selected: 0 });	$("#downloadXML").button();	var clientid 	=	0;		$('#downloadXML').on("click",function(e){		var data = validateXMLForm();		if(data.valid){			getXMLFile(data.data);			e.preventDefault()// cancel form submission			$('#downloadXML,#xmlWaitMsg').toggle();			return false;			}	});				if(datagridClients){			var usrClient = datagridClients.getSelectedRows();		if(usrClient.length > 0){			clientid = usrClient[0].id;		}	}	$.ajax({        type:'get',		url: apiUrl+ 'user/info',        dataType:"json",        async:false,        headers:{"Api-Key":apiKey,"User":userid},                success:function(data){	        			var phone = data.userInfo.phone.replace(/ /g, '');						$('#seller_name').val(data.userInfo.firstName +' '+data.userInfo.lastName); 			$('#seller_company').val(data.userInfo.corporationName); 			$('#seller_office').val(data.userInfo.officeName); 			/*$('#seller_phone1').val(phone.substr(phone.length-10, 3)); 			$('#seller_phone2').val(phone.substr(phone.length-7, 3)); 			$('#seller_phone3').val(phone.substr(phone.length-4, 4));*/ 			$('#seller_email').val(data.userInfo.email);         }	});	$.ajax({        type:'get',		url: apiUrl+ 'clientmanager/get/'+clientid,        dataType:"json",        async:false,        headers:{"Api-Key":apiKey,"User":userid},        success:function(data){			if(data.client !== null){				$('#bcompany').val(data.client.company);			}        }	});				function getXMLFile(data){	    $.ajax({	        type:'post',	        url: apiDldUrl+"xml",	        dataType:"json",	        headers:{"Api-Key":apiKey,"User":userid},	        processData: false,	        contentType: 'application/json',	        data: JSON.stringify(data),	        success:function(resp){		        if(resp.filename !== false){					var link  = 'services/fdownload.php?filename='+resp.filename;					$("#dialog-window").dialog("destroy");					window.location.href = link;				}				else{					closeAllDialogs();					loadDialogWindow('xmlerror', 'ShowSeeker XML Error Message', 450, 180, 1);									}	        },	        error:function(resp){		        console.log(rep);	        }	    });	};		function validateXMLForm(){				var data 	= {};		var r 		= {};		data.sellerName 	= $('#seller_name').val();		data.sellerCompany 	= $('#seller_company').val();		data.sellerOffice 	= $('#seller_office').val();		data.sellerPhone 	= '('+$('#seller_phone1').val()+')';		data.sellerPhone 	+=$('#seller_phone2').val()+'-';		data.sellerPhone 	+=$('#seller_phone3').val();		data.sellerMail		= $('#seller_email').val(); 		data.buyerName 		= $('#bname').val(); 		data.buyerCompany 	= $('#bcompany').val();		data.buyerOffice 	= $('#boffice').val();		data.buyerAdvertiser= $('#badvertiser').val();		data.buyerProduct 	= $('#bproduct').val();		data.demoType		= $('#dtype').val();		data.demoGroup 		= $('#group').val();		data.demoFrom 		= $('#agefrom').val();		data.demoTo 		= $('#ageto').val();		data.proposalId 	= proposalid;						if($('#seller_phone1').val().trim().length ===0 || $('#seller_phone2').val().trim().length ===0 || $('#seller_phone3').val().trim().length ===0){			$('#xmlTabs').tabs('select', 0);			r.valid = false;		}		else if(	data.sellerName 	=== '' ||			data.sellerCompany === '' ||			data.sellerOffice 	=== '' ||			data.sellerPhone	=== '' ||			data.sellerMail		=== ''){			$('#xmlTabs').tabs('select', 0);			r.valid = false;		}		else if( data.buyerName 		=== '' ||				 data.buyerCompany		=== '' ||				 data.buyerOffice		=== '' ||				 data.buyerAdvertiser	=== '' ||				 data.buyerProduct		=== ''){			$('#xmlTabs').tabs('select', 1);			r.valid = false;		}		else{			r.valid = true;					}		r.data = data;		return r;	}</script>