<?php 		$userid 	= $_POST['uid'];	$token 		= $_POST['apikey'];	$corp 		= $_POST['corp'];	$result 	= getPackageNames($userid,$corp,$token);	print($result);	exit;		function getPackageNames($userid,$corp,$token){		$ch 		= curl_init();		$timeout 	= 5;				$url 		= 'https://easygrids.showseeker.com/services/index.php';		if($corp == 22){			$url 		= 'https://easygrids.showseeker.com/services/byzone.php';		}				$data 		= "uid={$userid}&token={$token}";  		curl_setopt($ch, CURLOPT_URL, $url);		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);		curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);		curl_setopt($ch, CURLOPT_CUSTOMREQUEST, "POST");		curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);		curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);		curl_setopt($ch, CURLOPT_POSTFIELDS, $data);		curl_setopt($ch, CURLOPT_HTTPHEADER, array('Content-Type: application/x-www-form-urlencoded','Content-Length: ' . strlen($data)));		$data 		= curl_exec($ch);		curl_close($ch);		return $data;	}		?>