	<!-- div  class="top-bar data-topbar" style="display:table; height50px; width:100%; position:relative;" -->		<div class="row top-bar data-topbar">					<div class="large-4 columns">				<div class="row">					<div class="large-2 columns ez_columns">						<span class="label">Proposal</span>					</div>										<div class="large-7 columns ez_columns">						<input 	type="text" 			id="newproposal"		name="newproposal" >					</div>										<div class="large-3 columns ez_columns">											<a href="#" id="createproposal"  name="createproposal"  class="tiny button success radius">Create</a>					</div>				</div>			</div>						<div class="large-3 columns">				<div class="row">					<div class="large-12 columns">						<select id="proposalList" name="proposalList" ></select>   						</div>				</div>			</div>						<div class="large-3 columns">					<div class="row">					<div class="large-10 columns  ez_columns">									<select id="pdfGridOption">							<option value="1">Download/Print Current View</option>							<option value="2">Download / Print All Weeks</option>								</select>					</div>					<div class="large-2 columns  ez_columns">						<a href="#" id="printPdfGrid" name="printPdfGrid"  class="tiny	 button success radius">Go</a>					</div>				</div>								</div>			<div class="large-8 columns"></div>					</div>			<!-- /div -->      			<div class="row top-bar data-topbar">							<div class="large-2 columns">				<!-- label for="network-selector">Network</label -->				<div class="row">					<div class="large-3 columns">						<span class="label">Zone</span>					</div>					<div class="large-9 columns">											<select name="zone-selector" id="zone-selector">							<option value=""></option>									</select>						</div>										</div>			</div>				<div class="large-2 columns">				<!-- label for="network-selector">Network</label -->				<div class="row">					<div class="large-4 columns">						<span class="label">Network</span>					</div>					<div class="large-8 columns">											<select name="network-selector" id="network-selector">							<option value=""></option>									</select>						</div>										</div>			</div>			<div class="large-2 columns">				<!-- label for="startDate">Dates</label -->				<div class="row">					<div class="large-2 columns">						<span class="label">Dates</span>					</div>					<div class="large-4 columns">											<input name="startDate" id="startDate" type="text">					</div>											<div class="large-2 columns">						<span class="label">-</span>					</div>					<div class="large-4 columns">											<input name="endDate" 	id="endDate" 	type="text">					</div>				</div>			</div>						<div class="large-2 columns">				<!-- label for="stime">Time</label -->				<div class="row">					<div class="large-2 columns">						<span class="label">Time</span>					</div>					<div class="large-4 columns">						<select id="stime" name="stime">							<option value=""></option>									</select>					</div>					<div class="large-1 columns">						<span class="label">-</span>						</div>					<div class="large-5 columns">											<select id="etime" name="etime">							<option value=""></option>									</select>					</div>				</div>			</div>					<div class="large-2 columns">				<!-- label for="highlightThisType">Select</label -->				<div class="row">					<div class="large-3 columns">										<span class="label">Select</span>					</div>					<div class="large-7 columns">											<select id="highlightThisType">							<option value="All Shows">All Shows</option>							<option value="Sports Events Live">Sports Events Live</option>							<option value="Sports NonEvents Live">Sports NonEvents Live</option>							<option value="Premieres">Premieres</option>							<option value="Finales">Finales</option>							<option value="Movies">Movies</option>							<option value="Movie Premieres">Movie Premieres</option>							<option value="Only New">Only New</option>						</select>					</div>					<div class="large-2 columns">						<input type="checkbox" name="selectAllBoxes" id="selectAllBoxes"  value="0" name="selectAll" title="Check to select all the programs from the current Tab">					</div>									</div>			</div>			<div class="large-2 columns">				<div class="row">					<div class="large-12 columns">						<a href="#" id="updategrid" name="updategrid"  class="tiny	 button success radius">Update EzGrid</a>											</div>				</div>			</div>						</div>