<?php	//set the user settings	$tokenid = $_GET['tokenid'];	$zoneid = $_GET['zoneid'];	$userid = $_GET['userid'];		//if there is anything blank return an error	if(empty($tokenid) || empty($zoneid) || empty($userid)){		exit('error');	}	include_once('database.php');	$sql = "SELECT 	zones.id AS zoneid,	zones.name AS zonename,	zones.syscode,	zones.dmaid,	zones.timezoneid AS tzid,	timezones.name AS tzname,	timezones.abbreviation AS tzabbreviation,	zonenetworks.networkid AS id,	tms_networks.callsign,	tms_networks.name,	coalesce(logos.filename,'default.gif') AS filename	FROM zones	INNER JOIN timezones ON timezones.id = zones.timezoneid	INNER JOIN zonenetworks ON zones.id = zonenetworks.zoneid	INNER JOIN tms_networks ON zonenetworks.networkid = tms_networks.networkid	LEFT JOIN networklogos ON networklogos.networkid = tms_networks.networkid	LEFT JOIN logos ON logos.id = networklogos.logoid	WHERE zones.id = $zoneid AND zonenetworks.deletedat IS NULL AND zones.deletedat IS NULL	ORDER BY tms_networks.callsign	";	$result = mysql_query($sql);	$cnt = mysql_num_rows($result);	$array = mysql_fetch_assoc($result); 	$responseHeader = array(		"count" => $cnt,		"zoneid" => (int)$zoneid,		"zonename" => $array['zonename'],		"syscode" => $array['syscode'],		"dmaid" => $array['dmaid'],		"userid" => (int)$userid,		"tokenid" => $tokenid,		"tzid" => $array['tzid'],		"tzname" => $array['tzname'],    	"tzabbreviation" => $array['tzabbreviation']	);	mysql_data_seek($result, 0);	while($row = mysql_fetch_array($result)){		$network = array(			"name" => $row['name'],			"callsign" => $row['callsign'],			"id" => $row['id'],			"timnezoneid" => $row['tzid'],			"logob" => "http://ww2.showseeker.com/images/logos/".$row['filename'],			"logos" => "http://ww2.showseeker.com/images/_thumbnailsW/".$row['filename']		);		$networks[] = $network; 	}	$re = array("responseHeader"=>$responseHeader, "response"=>array("networks"=>$networks));	print json_encode($re);	return;?>