<?php	$userid = $_GET['userid'];	$tokenid = $_GET['tokenid'];		//if there is anything blank return an error	if(empty($tokenid) || empty($userid)){		exit('error');	}	include_once('database.php');		$sql = "SELECT	officedefaults.userid AS id,	officedefaults.officeid AS defaultoffice,	user_settings.settings,	users.corporationid,  	users.firstname, 	users.lastname, 	users.altid1,	regions.iseeker, 	regions.name,	regions.id AS regionsid,	corporations.ratecards,	corporations.ratecardmode	FROM officedefaults	INNER JOIN users ON users.id = officedefaults.userid	INNER JOIN corporations ON users.corporationid = corporations.id	INNER JOIN offices ON officedefaults.officeid = offices.id	INNER JOIN regions ON offices.regionid = regions.id	LEFT OUTER JOIN user_settings ON users.id = user_settings.userid	WHERE users.id = {$userid} AND users.tokenid = '{$tokenid}'";			$result = mysql_query($sql);	$array = mysql_fetch_assoc($result); 	$page = file_get_contents('http://ww2.showseeker.com/zones/listing.json?userid='.$userid.'&tokenid='.$tokenid.'');	//get the shares if any	$sql = "	SELECT *	FROM messages	WHERE userid = {$userid} AND hasread = 0";	//print_r($sql);	$result = mysql_query($sql);	$num_rows = mysql_num_rows($result);	//buttons	$button = getButtons($array['corporationid']);	//$json = json_encode($array);		$re = array(		"messages" => $num_rows,		"button" => $button,		"ratecards" => $array['ratecards'],		"ratecardmode" => $array['ratecardmode'],		"fname" => $array['firstname'],		"lname" => $array['lastname'],		"regionsid" => $array['regionsid'],		"iseeker" => $array['iseeker'],		"altid1" => $array['altid1'],		"corporationid" => $array['corporationid'],    	"settings" => $array['settings'],    	"zones" => $page	);	print json_encode($re);	function getButtons($corporationid){		$sql = "SELECT exports.name, exports.id		FROM corporationexports		INNER JOIN exports ON exports.id = corporationexports.exportid		WHERE  corporationexports.corporationid = $corporationid";		$result = mysql_query($sql);		while ($row = mysql_fetch_assoc($result)) {			$row['icon'] = "#download-images-".$row['name'];    		$re[] = $row;		}		return $re;	}?>