<?php	ini_set("display_errors",0);	$userid 		= $_GET['userid'];	$authtokin 	= $_GET['tokenid'];	$method 		= $_GET['method'];	//if there is anything blank return an error	if(empty($authtokin) || empty($userid)){		exit('error');	}		//include the classes and the database	include_once('../../config/mysqli.php');	//Authentication	require_once('../../classes/Auth.php');	$auth = new Auth($con);	$url = $_SERVER['PHP_SELF'];	$key = $auth->checkAuth($url,$authtokin,$userid);	if(!$key){		print "Access denied - You are not authorized to access this page.";		exit;	}		$sql 		= "SELECT 		users.corporationid, 									corporations.apikey 					FROM 			users 					INNER JOIN 	corporations 					ON 			corporations.id = users.corporationid 					WHERE 		users.id = $userid";	$result 	= mysqli_query($con, $sql);	$row 		= mysqli_fetch_assoc($result);	$apikey 	= $row['apikey'];		switch($method){		case 'revenue':		$re = revenue($apikey,$con);		break;				case 'clients':		$re = clients($apikey,$con);		break;	}			print json_encode($re);	function revenue($apikey,$con){	if($apikey == 'c08a7c8f-9fa7-4e01-98cf-f0602b07d83a'){		$tbl 	= "GCIRevenueTypes";	}	$sql 		= "SELECT 		id, 									label 					FROM 			Customers.$tbl 					ORDER BY 	label";		$result 	= mysqli_query($con,$sql);	$row_cnt = $result->num_rows;		if($row_cnt == 0)			$re[] = array();			else{		while ($row = mysqli_fetch_assoc($result)) {			$re[] = $row;		}	}	return $re;}function clients($apikey,$con){	$re = array();		if($apikey == 'c08a7c8f-9fa7-4e01-98cf-f0602b07d83a'){		$sql = ' SELECT 	SZCOMPANY AS label, 								SZCUSTNUMBER AS value 					FROM 		Customers.GCIData 					WHERE 	UCTYPE = "S" 					ORDER BY SZCOMPANY';							$result = mysqli_query($con, $sql);		while ($row = mysqli_fetch_assoc($result)) {	    	$customer[] = $array = array("label" => trim($row['label']),"value" => trim($row['value']));		}		$sql = '	SELECT 	SZCOMPANY AS label, 								SZCUSTNUMBER AS value 					FROM 		Customers.GCIData 					WHERE 	UCTYPE = "A" 					ORDER BY SZCOMPANY';		$result = mysqli_query($con, $sql);		while ($row = mysqli_fetch_assoc($result)) {	    	$agency[] = $array = array("label" => trim($row['label']),"value" => trim($row['value']));		}		$sql = '	SELECT 	id AS id, 								CONCAT_WS(" ",firstname,lastname) AS label,								altid1 AS value					FROM 		users 					WHERE 	corporationid = 16 					ORDER BY CONCAT_WS(" ",firstname,lastname)';		$result = mysqli_query($con, $sql);		while ($row = mysqli_fetch_assoc($result)) {	    	$users[] = $array = array("label" => trim($row['label']),"value" => trim($row['value']));		}		$re = array(			"form1" => $customer,			"form2" => $agency,			"form3" => $users		);	}	return $re;}?>