<?php	ini_set("display_errors",0);	$userid 		= $_GET['userid'];	$authtokin 	= $_GET['tokenid'];	//if there is anything blank return an error	if(empty($authtokin) || empty($userid)){		exit('error');	}	//include the classes and the database	include_once('../../config/mysqli.php');	//Authentication	require_once('../../classes/Auth.php');	$auth = new Auth($con);	$url = $_SERVER['PHP_SELF'];	$key = $auth->checkAuth($url,$authtokin,$userid);	if(!$key){		print "Access denied - You are not authorized to access this page.";		exit;	}		//set the token id for the user	$tokenid = $key;	$dbHost		= 'db5.showseeker.net';	$dbUserName	= 'vastdb';	$dbPassWord	= 'VastPlus#01';	$dbName		= 'showseeker';	$cnn = mysqli_connect($dbHost,$dbUserName,$dbPassWord,$dbName);	if (mysqli_connect_errno()){	  echo "Failed to connect to MySQL: " . mysqli_connect_error();	}	$sql = "	SELECT 	packageid, 							name, 							active, 							deleted, 							created, 							updated,							CASE 							WHEN u IS NULL OR u < c THEN DATE_FORMAT(c, '%m/%d/%Y') 							ELSE DATE_FORMAT(u, '%m/%d/%Y') 							END AS latest FROM (																					SELECT 	packageid,														packages.name,														packages.active,														DATE_FORMAT(MAX(programDataProjected.deletedat), '%m/%d/%Y') as deleted,														DATE_FORMAT(MAX(programDataProjected.createdat), '%m/%d/%Y') as created,														DATE_FORMAT(MAX(programDataProjected.updatedat), '%m/%d/%Y') as updated,														MAX(programDataProjected.createdat) as c,														MAX(programDataProjected.updatedat) as u											FROM		programDataProjected											INNER JOIN	packages											on			programDataProjected.packageid = packages.id											where 	packageid is not null and packageid > 0											and 		programDataProjected.deletedat is null											and		packages.deletedat is null											and		packages.active = 1															and 		programDataProjected.createdat is not null											GROUP BY packageid) UPDATES";	$result = mysqli_query($cnn, $sql);	if($result->num_rows == 0){			$data = array();	}	else{		//IF NOT RESULTS RETURN EMPTY ARRAY		while ($row = $result->fetch_assoc()) {   			$data[] = $row;    	}   }   	print json_encode($data);?>