<?php	ini_set("display_errors",1);	//set the userid and the token id and other varibles that are needed	$userid			= $_GET['userid'];	$authtokin 		= $_GET['tokenid'];		//if there is anything blank return an error	if(empty($authtokin) || empty($userid)){		exit('error');	}		//include the classes and the database	include_once('../../config/mysqli.php');	//Authentication	require_once('../../classes/Auth.php');	$auth = new Auth($con);	$url = $_SERVER['PHP_SELF'];	$key = $auth->checkAuth($url,$authtokin,$userid);	if(!$key){		print "Access denied - You are not authorized to access this page.";		exit;	}	//set the token id for the user	$tokenid = $key;	require_once('../../classes/User.php');	require_once('../../classes/Zones.php');		//create the user events	$user 			= new User($con,$userid,$tokenid);	$zone 			= new Zones($con,$userid,$tokenid);	$zonesbymarket = array();		//getting user markets	$markets			= $user->getmarkets();		//getting zones by market	foreach($markets as $market){		$zones 				= $zone->getzones($market["id"],0,1,0);		$zonesbymarket[]  = array("marketid" => $market["id"], "zones" => $zones);		/* $zoneids				= array();		foreach($zones as $thiszone){			$zoneids[] 		= $thiszone['id'];		}		$zonesbymarket[]  = array("marketid" => $market["id"], "zones" => $zoneids);*/	}		print json_encode($zonesbymarket);?>