<?php		class Broacastdates{	public function __construct(){	}	public function getMonday($thisDate){		return  (date('w',strtotime($thisDate))==1)?$thisDate:date('Y-m-d',strtotime('previous monday',strtotime($thisDate)));	}		public function getSunday($thisDate){		return date('Y-m-d',strtotime($this->getMonday($thisDate).' next sunday'));			}		public function numberOfWeeks($sDate,$eDate){		return floor((strtotime($eDate) - strtotime($sDate)) / (60*60*24*7));	}}?>