<?php		class Station{	protected $con;	//build the basic information about the logged in user	public function __construct($con){		$this->con = $con;	}			//--- USER ZONES --->	public function getInfo($networkid,$zoneid){				$sql = "select 			timezones.mysqlswitch, 								timezones.abbreviation, 								tms_networks.callsign, 								tms_networks.networkid				from 			tms_networks				inner join 		zonenetworks 				on				tms_networks.networkid = zonenetworks.networkid				inner join 		zones				on				zonenetworks.zoneid = zones.id				inner join 		timezones				on				zones.timezoneid = timezones.id				where			tms_networks.networkid = $networkid				and				zones.id = $zoneid";									$r		=mysqli_query($this->con,$sql);				$result = mysqli_fetch_assoc($r);		return $result;	}	}?>