<table cellpadding="5" align="center">	<tr>		<td align="right"><b>Name:</b></td>		<td><input class="rounded-corners forms noXButton" name="save-search-name" id="save-search-name" type="text" style="width:250px;"></td>	</tr>	<tr>		<td align="right"><b>Notes:</b></td>		<td><textarea  class="rounded-corners forms" name="save-search-notes" id="save-search-notes" rows="4" style="width:250px;height:50px;"></textarea></td>	</tr>	<tr>		<td align="right" nowrap="nowrap"><b>Filters:</b></td>		<td>			<span id="save-search-buttons">				<input type="radio" checked="checked" id="save-search-off" name="save-search-selector" value="off" class="searchmode" /><label for="save-search-off">Off</label>				<input type="radio" id="save-search-premieres" name="save-search-selector" value="premiere" class="searchmode" /><label for="save-search-premieres">Premieres</label>				<input type="radio" id="save-search-new" name="save-search-selector" value="new" class="searchmode" /><label for="save-search-new">New</label>				<input type="radio" id="save-search-reminder" name="save-search-reminder" value="0" style="display:none" />				<input type="radio" id="save-search-live" name="save-search-selector" value="live" class="searchmode" /><label for="save-search-live">Live</label>			</span>		</td>	</tr>	<tr>		<td align="right"><!-- b>Reminder:</b --></td>		<td>			<!-- The reminder function is being re-engineered and will be released in a future upgrade -->		</td>	</tr>	<tr>		<td colspan="2"><center>	<button class="btn-blue" onclick="saveSearch();" id="save-search-btn">Save</button>	<button class="btn-red" id="cancel-search-btn">Cancel</button></center></td>	</tr></table><script>$('#save-search-btn,#cancel-search-btn').button();$('#cancel-search-btn').click(function(){	$("#dialog-window").empty().dialog("destroy");});	$('#save-search-buttons').ready(function(){	$('#save-search-buttons').buttonset();	var params = solrSearchParamaters();	var name = saveSearchDefaultName(params);	$("#save-search-name").val(name);});</script>