'use strict';
angular.module('angular-simple-sidebar', []).directive('angularSimpleSidebar', function() {
    return {
        restrict: 'E',
        transclude: !0,
        scope: {
            uri: '=',
            state: '='
        },
        templateUrl: 'partials/showcard.html',
        link: function(a) {

            a.$watch('state', function(newValue) {
                if (newValue === true) {
                    a.slide = 'in';
                } else {
                    a.slide = 'out';
                }
            });

            a.closeSidebar = function() {
                a.state = !1;
                a.slide = 'out';
            };


        }
    };
});